mapper.update:
UPDATE BOARD
TITLE = #{title}, CONTENTS = #{contents}, MOD_DATE = SYSDATE
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD

	RECOMMEND_COUNT = RECOMMEND_COUNT + 1,		

WHERE BOARD_NO = #{boardNo}

UPDATE BOARD

	RECOMMEND_COUNT = RECOMMEND_COUNT - 1,		

WHERE BOARD_NO = #{boardNo}

UPDATE BOARD

	HITS = HITS + 1,		

WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT HITS_NO, BOARD_NO, MEMBER_NO, CRE_DATE, SYSDATE-CRE_DATE AS TIME_DIFFERENCE
FROM HITS
WHERE BOARD_NO = #{boardNo} 
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE HITS

	CRE_DATE = SYSDATE		

WHERE HITS_NO = #{hitsNo}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{no}
