mapper.select:
SELECT RE.RESTAURANT_NO, RE.RESTAURANT_NAME, RE.CATEGORY, RE.STORED_FILE_NAME FROM (SELECT R.RESTAURANT_NO, R.RESTAURANT_NAME, R.CATEGORY, F.STORED_FILE_NAME FROM RESTAURANTS R LEFT OUTER JOIN TB_FILE F ON R.RESTAURANT_NO = F.PARENT_SEQ
ORDER BY R.RESTAURANT_NO DESC
ORDER BY R.RESTAURANT_NAME ASC
) RE WHERE ROWNUM BETWEEN 1 AND #{end}

SELECT RESTAURANT_NO, MEMBER_NO, RESTAURANT_NAME, CATEGORY, PHONE, OPERATING_TIME, CLOSING_TIME, CLOSED_DATES, ADDRESS, CONTENTS
FROM RESTAURANTS
WHERE RESTAURANT_NO = #{restaurantNo}

mapper.update:
UPDATE RESTAURANTS
RESTAURANT_NAME = #{restaurantName}, CATEGORY = #{category}, PHONE = #{phone}, OPERATING_TIME = #{operatingTime}, CLOSING_TIME = #{closingTime}, CLOSED_DATES = #{closedDates}, ADDRESS = #{address}, CONTENTS = #{contents}, MOD_DATE = SYSDATE
WHERE RESTAURANT_NO = #{restaurantNo}

mapper.select:
SELECT IDX, ORIGINAL_FILE_NAME, STORED_FILE_NAME, ROUND(FILE_SIZE/1024, 1) AS FILE_SIZE
FROM TB_FILE
WHERE PARENT_SEQ = #{restaurantNo}

SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{restaurantNo}
